def find_median(lst):
    for i in range(len(lst)):
        for j in range(i+1, len(lst)):
            if lst[i] > lst[j]:
                lst[i], lst[j] = lst[j], lst[i]

    n = len(lst)
    if n % 2 == 0:
        median = (lst[n//2-1] + lst[n//2]) / 2
    else:
        median = lst[n//2]

    return median

