    try {
        for(int i = 0; i < reversed.length(); i++){
            if (!isalpha(reversed[i])) {
                throw "Invalid character found in the string.";
            }
            reversed[i] = toupper(reversed[i]);
        }

        cout << reversed << endl;
    } catch (const char* error) {
        cout << "Error: " << error << endl;
    }

    return 0;
}

